import React from 'react'
import { useState, useEffect } from 'react'
import Nav from '../Nav/Nav'
import Footer from "../Footer/Footer"
import "./Products.css"
import axios from 'axios';
function Products() {


    const [data, setData] = useState({ hits: [] });
    const [query, setQuery] = useState('redux');
    // https://60678eaf98f405001728ef55.mockapi.io/api/v1/product/

    const [posts, setPosts]=useState([])
    const getPosts = async () => {
      try {
    const userPosts = await axios.get("https://60678eaf98f405001728ef55.mockapi.io/api/v1/product/")
        
    setPosts(userPosts.data);  // set State
      
      } catch (err) {
        console.error(err.message);
      }
    };
      
      useEffect(()=>{
        
        getPosts()
    },[])  // includes empty dependency array
    return (
        <div>
            <Nav />
            <div className='content'>products
                </div>
            <ul class="caption-style-1">

            {/* {posts.map(post=>(
         <li key={post.id}>{post.image}</li>
       ))} */}
       { posts.map(post=>(
         <li key={post.id}>
           <img className='list-photo' src={post.image} alt={post.name}/>
           <div class="caption">
					<div class="blur"></div>
					<div class="caption-text">
						<h1>Amazing Caption</h1>
						<p>Whatever It Is - Always Awesome</p>
					</div>
				</div>
         </li>
       ))
         
       }
          
            </ul>
            <Footer />
        </div>
    )
}

export default Products
