                </div>
            <ul>

            {posts.map(post=>(
         <li key={post.id}>{post.image}</li>
       ))}
       { posts.map(post=>(
         <li key={post.id}>
           <image src={post.image} alt={post.name}></image>
         </li>
       ))
         
       }
          
            </ul>
            <Footer />
        </div>
    )
}

export default Products
